import React from 'react';

class ButtonAlert extends React.Component {
constructor(){
  super();

  this.state = {
    txt: "Mi texto en estado inicial"
  }
}

  updateTXT(e) {
    this.setState({txt: e.target.valeu})
  }

  render() {
    return (
      <>
         <input className="button" type="text" onChange={this.updateTXT.bind(this)} />
        <h1>{this.state.txt}</h1>
     </>
    );
  }
}

export default ButtonAlert;
