import React from 'react';

class InputState extends React.Component {
  constructor() {

    this.state = {
        txt: 'Este es el estado de txt'
    }
}

    update(e) {
        this.setState({txt: e.target.value})
    }

    render() {
    return (
      <div className="input">
            <h2>Ejerc√≠cio 01:</h2>
            <input className="inputBox" type="text" onChange={this.update.bind(this)} />
            <h1>{this.state.txt}</h1>
      </div>
    )
   }
}

export default InputState;
