import React from 'react';

class SheepCounter extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            number: 0,
            image: []
        }
        this.handleClickCounter = this.handleClickCounter.bind(this);
    }

    handleClickCounter() {
        this.setState((prevState, index)=> {
            return {
                number: prevState.number + 1,
                image: [...prevState.image, <li key={this.state.image[index]}className="mySheeps"><img className="sheeps" src="http://www.clker.com/cliparts/e/4/8/7/13280460782141411990Cartoon%20Sheep.svg.hi.png" alt="One little sheep"/></li>]
            };
        });
    }

    render() {
        return (
            <div className="input">
                <h2>___EjercÃ­cio 04:___</h2>
                <h1>{this.state.number}</h1>
                <ul >{this.state.image}</ul>
                <button className="btn" onClick={this.handleClickCounter}>Counter</button>
                <button className="btn" onClick={this.handleReset.bind(this)}>Reset</button>
            </div>
        )
    }
}

export default SheepCounter;
