import React from 'react';

class Form extends React.Component {
    constructor() {
        super();
        this.state = {
            firstName: 'Ada',
            lastName: 'Lovelace',
            birthDate: {
                day: 10,
                month: 'diciembre',
                year: 1815
            }
        }
        this.updateClock = this.updateClock.bind(this);
    }

    update(e) {
        this.setState({ 
            firstName: e.target.value,
            lastName: e.target.value,
         })
      }

    render() {
        return (
            <>
                <div>
                    <h2>___Ejercício 06:__</h2>
                    <h3>Datos del usuário:</h3>
                    <p>{this.state.firstName}</p>
                    <p>{this.state.lastName}</p>
                    <div>
                        <span>{`${this.state.birthDate.day}/`}</span>
                        <span>{`${this.state.birthDate.month}/`}</span>
                        <span>{this.state.birthDate.year}</span>
                    </div>
                </div>
                <form>
                    <label>Nombre:</label>
                    <input type="text" onChange={this.update}/>
                    <label>Apellido:</label>
                    <input type="text" onChange={this.update} />

                </form>
            </>
        );
    }
}

export default Form;
