import React from 'react';

class Cuadrado extends React.Component {
  constructor() {
    super();
      this.state = {
          bg: 'blue'
      };
    this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        this.setState((prevState, props) => { 
            let secondColor;
            if (prevState.bg === 'blue') {
                secondColor = 'red';
            } else {
                secondColor = 'blue';
            }

            return {
                bg: secondColor
            };
        });
    }

    render() {
        return (
           <> 
            <h2>Ejerc√≠cio 01:</h2>
                <div className={`square color-${this.state.bg}`} onClick={this.handleClick}>Click Me!</div>
           </>
        );
    }
}

export default Cuadrado;
