import React from 'react';

class SheepCounter extends React.Component {
    constructor() {
        super();
        this.state = {
            number: 0,
            imagen: <img src="http://www.clker.com/cliparts/e/4/8/7/13280460782141411990Cartoon Sheep.svg.hi.png" />
        };
        this.handleClickCounter = this.handleClickCounter.bind(this);
    }

    handleClickCounter() {
        this.setState((prevState) => {
            return {
                number: prevState.number + 1
            };
        });
    }

    sumaImagen() {
        this.setState((prevState) => {
            return {
                imagen: prevState.imagen.repeat(handleClickCounter) 
            };
        });
    }


    render() {
        return (
            <div className="input">
                <h2>___Ejerc√≠cio 04:___</h2>
                <div>{this.state.imagen}</div>
                <h1>{this.state.number}</h1>
                <button className="btn" onClick={this.handleClickCounter, this.sumaImagen} >Counter</button>
            </div>
        )
    }
}

export default SheepCounter;
