import React from 'react';

class ButtonAlert extends React.Component {
  constructor() {
    super();
    this.state = {
        txt: 'Este es el estado de txt'
    }
}

    update(e) {
        this.setState({txt: e.target.value})
    }

    render() {
    return (
      <div className="input">
            <input className="inputBox" type="text" onChange={this.update.bind(this)} />
            <h1>{this.state.txt}</h1>
      </div>
    )
   }
}

export default ButtonAlert;
